import { Component } from "react";
import "./App.css";

class App extends Component {
  constructor() {
    super();
    this.state = {
      monsters: [],
    };
  }

  componentDidMount() {
    fetch("https://jsonplaceholder.typicode.com/users")
      .then((response) => response.json())
      .then((user) =>
        this.setState(
          () => {
            return { monsters: user };
          },
          () => {
            console.log("current State:- ", this.state); //check the current state of state object
          }
        )
      );
  }

  render() {
    return (
      <div className="App">
        <input
          className="search"
          type="search"
          placeholder="search monsters"
          onChange={(event) => {
            console.log("print-event-input", event.target.value);
            const filerByMonsterName = this.state.monsters.filter((monster) => {
              return monster.name.toL.includes(event.target.value);
            });

            this.setState(() => {
              return { monsters: filerByMonsterName };
            });
          }}
        />
        {this.state.monsters.map((monster) => {
          return (
            <div key={monster.id}>
              <h1>{monster.name}</h1>
            </div>
          );
        })}
      </div>
    );
  }
}

export default App;
