import { Component } from "react";
import "./App.css";

class App extends Component {
  constructor() {
    super();
    this.state = {
      monsters: [],
      searchField: "",
    };
  }

  componentDidMount() {
    fetch("https://jsonplaceholder.typicode.com/users")
      .then((response) => response.json())
      .then((user) =>
        this.setState(
          () => {
            return { monsters: user };
          },
          () => {
            console.log("current State:- ", this.state); //check the current state of state object
          }
        )
      );
  }

  render() {
    const filerByMonsterName = this.state.monsters.filter((monster) => {
      return monster.name.toLocaleLowerCase().includes(this.state.searchField);
    });

    return (
      <div className="App">
        <input
          className="search"
          type="search"
          placeholder="search monsters"
          onChange={(event) => {
            console.log("starting array:- ", this.state.monsters);
            const searchField = event.target.value.toLocaleLowerCase();

            this.setState(
              () => {
                return { searchField };
              },
              () => {
                console.log("Ending Array Status:-", this.state.monsters); //latest state
              }
            );
          }}
        />
        {this.state.filerByMonsterName.map((monster) => {
          return (
            <div key={monster.id}>
              <h1>{monster.name}</h1>
            </div>
          );
        })}
      </div>
    );
  }
}

export default App;
